mapper.insert:
insert into parkingmate (
license, history, picture,
addr, bank, account, id
) values (
#{license}, #{history}, #{picture},
#{addr}, #{bank}, #{account}, #{id}
)

mapper.select:
select * from mate_paycheck
where mid = #{mid}

	and starttime >= #{startDate}


	and endtime <= #{endDate}

order by starttime desc
